Vue.prototype.$emitWithPromise=function(topic,varargs){return new Promise((resolve,reject)=>{const arr=Array.from(arguments);let invoked=!1;const promiseLike={resolve:val=>{if(!invoked){invoked=!0;resolve(val)}},reject:val=>{if(!invoked){invoked=!0;reject(val)}}};arr.push(promiseLike);this.$emit.apply(this,arr)})}